Citizen.CreateThread(function()
	while true do

		Citizen.Wait(10)

		if CurrentAction ~= nil then
			ESX.ShowHelpNotification(CurrentActionMsg)

			if IsControlJustReleased(0, Keys['E']) and PlayerData.job ~= nil and PlayerData.job.name == 'cartel' then

				if CurrentAction == 'menu_cloakroom' then
					OpenCloakroomMenu()
				elseif CurrentAction == 'menu_ermonytel' then
					if RW.MaxInService == -1 then
						OpenermonytelMenu(CurrentActionData.station)
					elseif playerInService then
						OpenermonytelMenu(CurrentActionData.station)
					else
						ESX.ShowNotification(_U('service_not'))
					end
				elseif CurrentAction == 'menu_vehicle_spawner' then
					OpenmotorcartelpawnerMenu(CurrentActionData.station, CurrentActionData.partNum)
				elseif CurrentAction == 'delete_vehicle' then
					if RW.EnableSocietyOwnedmotorcartel then
						local vehicleProps = ESX.Game.GetVehicleProperties(CurrentActionData.vehicle)
